mapper.select:
SELECT a.student_id,a.student_name,a.telephone,a.department,a.major,a.begin_time,a.end_time,a.reason,a.is_agree
FROM content a,class b WHERE a.is_agree='wait' AND LEFT(a.student_id,10) = b.id AND b.teacher_id = #{id}
ORDER BY a.begin_time DESC

SELECT a.student_id,a.student_name,a.telephone,a.department,a.major,a.begin_time,a.end_time,a.reason,a.is_agree
FROM content a,class b WHERE LEFT(a.student_id,10) = b.id AND b.teacher_id = #{id}
ORDER BY a.begin_time DESC

SELECT a.student_id,a.student_name,a.telephone,a.department,a.major,a.begin_time,a.end_time,a.reason,a.is_agree
FROM content a WHERE a.user_id = #{myId}
ORDER BY a.begin_time DESC
